const int NUMFRAMES = 4800;
const float initX = 0, initY = -5, initZ = -25;
const float initRoll = -48, initYaw = 90, initPitch = 0;

GLUquadric* qobj;

void initTrajectory(void)
{
    X = initX;
    Y = initY;
    Z = initZ;
    roll = initRoll;
    pitch = initPitch;
    yaw = initYaw;
    totalStep = 0;
    IndexT = 0;
}

//drawing circle
void circle(float r, int npoints)
{
    float ang = 0;
    float dang = float(2*M_PI/npoints);
    glBegin(GL_TRIANGLE_FAN);
        glVertex3f(0, 0, 0); // center
        glVertex3f(r, 0, 0);
        for (int i = 1; i < npoints; i++)
        {
            ang += dang;
            glVertex3f(r*cosf(ang), r*sinf(ang), 0);
        }
        glVertex3f(r, 0, 0);
    glEnd();
}


void wheel(void)
{
    glPushMatrix();
        glColor3ub(20,20,20);
        glRotatef(90,0,1,0);
        glutSolidTorus(0.2, 0.4, 8, 24);
        glColor3ub(150,150,190);
        glutSolidSphere(0.2,16,8);
    glPopMatrix();
}


